import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Table, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { FcCheckmark, FcHighPriority } from 'react-icons/fc';

import {
  fetchDistinctOrderId,
  fetchOneOrder,
} from '../../actions/orderActions';

const OrderListScreen = () => {
  const history = useHistory;
  const dispatch = useDispatch();

  // STATES
  const [orderList, setOrderList] = useState([]);

  // Global state
  const { distinctIds } = useSelector((state) => state.orderFetchDistinct);
  const { order } = useSelector((state) => state.orderFetchOne);

  useEffect(() => {
    // * ~~~ I got all the distinct IDs
    dispatch(fetchDistinctOrderId());

    distinctIds.forEach((idObj) => {
      dispatch(fetchOneOrder(idObj.order_id));
    });
  }, [dispatch]);

  useEffect(() => {
    let orderz = [];
    if (order) {
      orderz = [...orderz, order];
      setOrderList(orderz);
    }
    console.log('yawa basad', order);
  }, [order]);

  return (
    <div>
      <h1>Orders</h1>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>ID</th>
            <th>USER</th>
            <th>DATE</th>
            <th>TOTAL</th>
            <th>PAID</th>
            <th>DELIVERED</th>
            <th colSpan="2" className="text-center">
              ACTIONS
            </th>
          </tr>
        </thead>
        <tbody>
          {orderList.map((orderItem) => (
            <tr>
              <td>{orderItem.order_id}</td>
              <td>{orderItem.name}</td>
              <td>{orderItem.created_at}</td>
              <td>{orderItem.subtotal}</td>
              <td>
                {orderItem.is_paid ? (
                  <FcCheckmark size="2em" />
                ) : (
                  <FcHighPriority size="2em" />
                )}
              </td>
              <td>
                {orderItem.is_delivered ? (
                  <FcCheckmark size="2em" />
                ) : (
                  <FcHighPriority size="2em" />
                )}
              </td>
              <td>
                <Button>Details</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default OrderListScreen;
