import React from 'react';
import { Row, Col, Button, Table } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { MdAddCircleOutline, MdDeleteForever } from 'react-icons/md';
import { FaRegEdit } from 'react-icons/fa';

const ProductListScreen = () => {
  const dispatch = useDispatch();
  const { products } = useSelector((state) => state.productList);
  return (
    <>
      <Row className="align-items-center">
        <Col>
          <h1>Products</h1>
        </Col>
        <Col className="text-right">
          <Button className="btn-sm">
            <MdAddCircleOutline size="3em" style={{ marginRight: '5px' }} />
            Create Product
          </Button>
        </Col>
      </Row>
      <Table striped bordered hover responsive className="mt-2 table-sm">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Category</th>
            <th>Brand</th>
            <th colSpan="2" className="text-center">
              Actions
            </th>
          </tr>
        </thead>
        <tbody>
          {products.length ? (
            products.map((product) => {
              <tr>
                <td>3</td>
                <td>Larry the Bird</td>
                <td>@twitter</td>
                <td>@twitter</td>
                <td>@twitter</td>
                <td>
                  <Button variant="light" className="btn-sm">
                    <FaRegEdit size="2em" color="blue" />
                    Edit
                  </Button>
                </td>
                <td>
                  <Button variant="light" className="btn-sm">
                    <MdDeleteForever size="2em" color="red" />
                    Delete
                  </Button>
                </td>
              </tr>;
            })
          ) : (
            <h1>No Products found!</h1>
          )}
        </tbody>
      </Table>
    </>
  );
};

export default ProductListScreen;
