import React, { useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { FaShoppingCart, FaUserCircle } from 'react-icons/fa';
import { useDispatch, useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap';
import { logout } from '../actions/userActions';

const CustomNavbar = () => {
  const history = useHistory();
  const dispatch = useDispatch();
  const { userInfo } = useSelector((state) => state.userLogin);
  const { user } = useSelector((state) => state.userDetails);

  useEffect(() => {}, [user]);

  const logoutHandler = () => {
    dispatch(logout());
    history.push('/login');
  };
  return (
    <Navbar bg="dark" variant="dark" expand="lg" colapseOnSelect>
      <Container>
        <Link to="/">
          <Navbar.Brand>Raboy's E-Shop</Navbar.Brand>
        </Link>
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link onClick={() => history.push('/cart')}>
              <FaShoppingCart /> Cart
            </Nav.Link>

            {userInfo ? (
              <NavDropdown
                title={user.email ? userInfo.email : userInfo.email}
                id="email"
              >
                <LinkContainer to="/profile">
                  <NavDropdown.Item>Profile</NavDropdown.Item>
                </LinkContainer>
                <NavDropdown.Item onClick={logoutHandler}>
                  Logout
                </NavDropdown.Item>
              </NavDropdown>
            ) : (
              <Nav.Link onClick={() => history.push('/login')}>
                <FaUserCircle /> Sign In
              </Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomNavbar;
