import { productConstants } from '../constants/productConstants'

const {PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS, PRODUCT_LIST_FAIL} = productConstants


export const listProducts = () => async (dispatch) => {
      try {
          dispatch({type: PRODUCT_LIST_REQUEST});

          const result = await fetch('http://localhost:5000/api/v1/products',{
            method: 'GET', 
            headers: {
                'Content-Type': 'application/json'
            }
        })

        const {data} = await result.json();

        dispatch({type: PRODUCT_LIST_SUCCESS, payload: data});

      } catch (error) {
        dispatch({
            type: PRODUCT_LIST_FAIL, 
            payload: error.response && error.response.data.message ? error.response.data.message : error.Error.message
        });
      }
}
