import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Table } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';

import { fetchDistinctOrderId } from '../../actions/orderActions';

const OrderListScreen = () => {
  const history = useHistory;
  const dispatch = useDispatch();

  // STATES
  const [orderList, setOrderList] = useState([]);

  // Global state
  const { distinctIds } = useSelector((state) => state.orderFetchDistinct);

  useEffect(() => {
    dispatch(fetchDistinctOrderId());
  }, [dispatch]);
  return (
    <div>
      <h1>Orders</h1>
      {distinctIds && distinctIds[0]}
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>ID</th>
            <th>USER</th>
            <th>DATE</th>
            <th>TOTAL</th>
            <th>PAID</th>
            <th>DELIVERED</th>
            <th colSpan="2" className="text-center">
              ACTIONS
            </th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
            <td>@mdo</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default OrderListScreen;
