import { orderConstants } from '../constants/orderConstants';

const {
  ORDER_CREATE_REQUEST,
  ORDER_CREATE_SUCCESS,
  ORDER_CREATE_FAIL,
  ORDER_FETCH_REQUEST,
  ORDER_FETCH_SUCCESS,
  ORDER_FETCH_FAIL,
  ORDER_DETAILS_FETCH_REQUEST,
  ORDER_DETAILS_FETCH_SUCCESS,
  ORDER_DETAILS_FETCH_FAIL,
} = orderConstants;

export const orderCreateReducer = (state = {}, { type, payload }) => {
  switch (type) {
    case ORDER_CREATE_REQUEST:
      return { loading: true };

    case ORDER_CREATE_SUCCESS:
      return { loading: false, success: true, order: payload };

    case ORDER_CREATE_FAIL:
      return { loading: false, error: payload };

    default:
      return state;
  }
};

export const orderFetchReducer = (
  state = { orderItems: [] },
  { type, payload }
) => {
  switch (type) {
    case ORDER_FETCH_REQUEST:
      return { loading: true };

    case ORDER_FETCH_SUCCESS:
      return { loading: false, success: true, orderItems: payload };

    case ORDER_FETCH_FAIL:
      return { loading: false, error: payload };

    default:
      return state;
  }
};

export const orderDetailsFetchReducer = (
  state = { orderDetailsList: [] },
  { type, payload }
) => {
  switch (type) {
    case ORDER_DETAILS_FETCH_REQUEST:
      return { loading: true };

    case ORDER_DETAILS_FETCH_SUCCESS:
      return {
        loading: false,
        success: true,
        orderDetailsList: [...state.orderDetails.orderDetailsList, { payload }],
      };

    case ORDER_DETAILS_FETCH_FAIL:
      return { loading: false, error: payload };

    default:
      return state;
  }
};
