import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { FaShoppingCart, FaUserAlt, FaUserCircle } from 'react-icons/fa';
import { useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap';

const CustomNavbar = () => {
  const history = useHistory();
  const { userInfo } = useSelector((state) => state.userLogin);
  return (
    <Navbar bg="dark" variant="dark" expand="lg" colapseOnSelect>
      <Container>
        <Link to="/">
          <Navbar.Brand>Raboy's E-Shop</Navbar.Brand>
        </Link>
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link onClick={() => history.push('/cart')}>
              <FaShoppingCart /> Cart
            </Nav.Link>

            {userInfo ? (
              <NavDropdown title="jhimson12" id="email">
                <LinkContainer to="/profile">
                  <NavDropdown.Item>Profile</NavDropdown.Item>
                </LinkContainer>
                <NavDropdown.Item onClick={() => {}}>Logout</NavDropdown.Item>
              </NavDropdown>
            ) : (
              <Nav.Link onClick={() => history.push('/login')}>
                <FaUserCircle />
                Sign In
              </Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomNavbar;
