import React, { useState, useEffect, useRef } from 'react';
import { Form } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { filterProducts, listProducts } from '../actions/productActions';

const SearchBox = () => {
  const dispatch = useDispatch();
  const [search, setSearch] = useState('');
  const productsObject = useSelector((state) => state.productList);
  const { products } = productsObject;
  const [filteredProducts, setFilteredProducts] = useState([]);

  const tempProducts = products.filter(
    (product) => product.name.toLowerCase().indexOf(search) !== -1
  );

  console.log(tempProducts);
  const searchHandler = (e) => {
    setSearch(e.target.value.substr(0, 20).toLowerCase());

    setFilteredProducts(tempProducts);
    dispatch(filterProducts(filteredProducts));
  };

  return (
    <Form onSubmit={searchHandler} inline>
      <Form.Control
        type="text"
        placeholder="Search..."
        value={search}
        onChange={searchHandler}
        className="mr-sm-2 ml-sm-5"
      />
      <Button type="submit" variant="outline-success">
        Search
      </Button>
    </Form>
  );
};

export default SearchBox;
