/* eslint-disable camelcase */
import React, { useState, useEffect } from 'react';
import { Link, useLocation, useParams } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap';
import Message from '../Message';
import Loader from '../Loader';
import FormContainer from '../FormContainer';
import { getUserToUpdate } from '../../actions/userActions';

const UserEditScreen = () => {
  const location = useLocation();
  const dispatch = useDispatch();
  const { firstname: fn, lastname: ln, username: un, email: em } = useSelector(
    (state) => state.userUpdate.userToUpdate
  );

  const { id: user_id } = useParams();
  const [email, setEmail] = useState('');
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState(null);

  useEffect(() => {
    if (!fn) {
      dispatch(getUserToUpdate(user_id));
    } else {
      setFirstname(fn);
    }
  }, [dispatch, user_id, fn]);
  return (
    <>
      <LinkContainer to="/admin/users">
        <Button>Go Back</Button>
      </LinkContainer>
      <FormContainer>
        <h1>Update User Account</h1>
        <Form>
          <Form.Group controlId="userId">
            <Form.Label>User ID</Form.Label>
            <Form.Control type="text" value={user_id} disabled />
          </Form.Group>
          <Form.Group controlId="firstname">
            <Form.Label>Firstname</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter Firstname"
              value={firstname}
              onChange={(e) => setFirstname(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="lastname">
            <Form.Label>Lastname</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter Lastname"
              value={lastname}
              onChange={(e) => setLastname(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="username">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </Form.Group>
          <Button type="submit" variance="primary">
            Update
          </Button>
        </Form>
      </FormContainer>
    </>
  );
};

export default UserEditScreen;
