/* eslint-disable camelcase */
import Axios from 'axios';
import { orderConstants } from '../constants/orderConstants';

const {
  ORDER_CREATE_REQUEST,
  ORDER_CREATE_SUCCESS,
  ORDER_CREATE_FAIL,
  ORDER_FETCH_REQUEST,
  ORDER_FETCH_SUCCESS,
  ORDER_FETCH_FAIL,
  ORDER_DETAILS_FETCH_REQUEST,
  ORDER_DETAILS_FETCH_SUCCESS,
  ORDER_DETAILS_FETCH_FAIL,
} = orderConstants;

export const createOrder = (order) => async (dispatch, getState) => {
  const { token } = getState().userLogin.userInfo;
  try {
    dispatch({ type: ORDER_CREATE_REQUEST });

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
    };

    const { data } = await Axios.post(
      `http://localhost:5000/api/v1/orders`,
      order,
      config
    );
    dispatch({ type: ORDER_CREATE_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: ORDER_CREATE_FAIL,
      payload:
        error.response && error.response.data.error.message
          ? error.response.data.error.message
          : error.message,
    });
  }
};

export const getOrdersByUserId = (user_id) => async (dispatch, getState) => {
  console.log(user_id);
  const { token } = getState().userLogin.userInfo;
  try {
    dispatch({ type: ORDER_FETCH_REQUEST });

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
    };
    const { data } = await Axios.get(
      `http://localhost:5000/api/v1/orders`,
      config
    );
    dispatch({ type: ORDER_FETCH_SUCCESS, payload: data.rows });
  } catch (error) {
    dispatch({
      type: ORDER_FETCH_FAIL,
      payload:
        error.response && error.response.data.error.message
          ? error.response.data.error.message
          : error.message,
    });
  }
};

export const getOrdersByOrderId = (order_id) => async (dispatch, getState) => {
  const { token, user_id } = getState().userLogin.userInfo;
  try {
    dispatch({ type: ORDER_DETAILS_FETCH_REQUEST });

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
    };
    const { data } = await Axios.post(
      `http://localhost:5000/api/v1/orders/orderDetails`,
      { user_id, order_id },
      config
    );

    dispatch({ type: ORDER_DETAILS_FETCH_SUCCESS, payload: data.rows });

    let orders = JSON.parse(localStorage.getItem('orderDetailsList')) || [];
    // let orders = [];
    if (orders.length === 0) {
      console.log('WALAY SULOD!');
      orders = [...orders, data.rows];
      console.log('TANG INA MO! BOBO K!');
    } else {
      orders.forEach((order) => {
        if (order.order_id !== data.rows.order_id) {
          orders = [...orders, data.rows];
        }
      });
    }

    localStorage.setItem('orderDetailsList', JSON.stringify(orders));
  } catch (error) {
    dispatch({
      type: ORDER_DETAILS_FETCH_FAIL,
      payload:
        error.response && error.response.data.error.message
          ? error.response.data.error.message
          : error.message,
    });
  }
};
