/* eslint-disable no-nested-ternary */
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Row, Col } from 'react-bootstrap';
import Product from '../Product';
import { listProducts } from '../../actions/productActions';
import Loader from '../Loader';
import Message from '../Message';
import { getOrdersByOrderId } from '../../actions/orderActions';

const HomeScreen = () => {
  const dispatch = useDispatch();

  const { orderItems } = useSelector((state) => state.orderFetch);

  const { loading, error, products } = useSelector(
    (state) => state.productList
  );

  const { orderDetailsList } = useSelector((state) => state.orderDetails);

  let uniqueOrderId = [];
  const orderListById = [];

  useEffect(() => {
    dispatch(listProducts());
    if (orderItems) {
      orderItems.forEach((order) => {
        if (!uniqueOrderId.includes(order.order_id)) {
          uniqueOrderId = [...uniqueOrderId, order.order_id];
        }
      });

      console.log('uniqueOrderIds', uniqueOrderId);
      uniqueOrderId.forEach((id) => {
        dispatch(getOrdersByOrderId(id));
      });
      console.log(orderListById);
    }
  }, [dispatch, orderDetailsList]);
  return (
    <>
      <h1>Featured Products</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : products.length === 0 ? (
        <Message>No Products found!</Message>
      ) : (
        <Row>
          {products.map((product) => (
            <Col key={product.product_id} sm={12} md={6} lg={4} xl={3}>
              <Product product={product} />
            </Col>
          ))}
        </Row>
      )}
    </>
  );
};

export default HomeScreen;
