import Axios from 'axios';
import { productConstants } from '../constants/productConstants'

const {PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS, PRODUCT_LIST_FAIL,PRODUCT_DETAILS_REQUEST,PRODUCT_DETAILS_SUCCESS,PRODUCT_DETAILS_FAIL} = productConstants


export const listProducts = () => async (dispatch) => {
      try {
          dispatch({type: PRODUCT_LIST_REQUEST});

          const response = await Axios.get('http://localhost:5000/api/v1/products');
        
        console.log(response);
        dispatch({type: PRODUCT_LIST_SUCCESS, payload: response.data});

      } catch (error) {
        console.log(error.message)
        dispatch({
            type: PRODUCT_LIST_FAIL, 
            payload: error.response && error.response.data.error.message ? error.response.data.error.message : error.message
        });
      }
}

export const listProductDetails = (product_id) => async (dispatch) => {
  try {
      dispatch({type: PRODUCT_DETAILS_REQUEST});

      const response = await Axios.get(`http://localhost:5000/api/v1/products/${product_id}`);
    
    console.log(response);
    dispatch({type: PRODUCT_DETAILS_SUCCESS, payload: response.data});

  } catch (error) {
    console.log(error.message)
    dispatch({
        type: PRODUCT_DETAILS_FAIL, 
        payload: error.response && error.response.data.error.message ? error.response.data.error.message : error.message
    });
  }
}
