/* eslint-disable no-nested-ternary */
import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useParams, useHistory } from 'react-router-dom';
import { Button, Row, Col, Image, ListGroup, Form } from 'react-bootstrap';
import Rating from '../Rating';
import { listProductDetails } from '../../actions/productActions';
import { fetchReviews } from '../../actions/reviewActions';
import Loader from '../Loader';
import Message from '../Message';

const ProductScreen = () => {
  const [qty, setQty] = useState(1);
  const { id } = useParams();
  const history = useHistory();
  const dispatch = useDispatch();
  const { product, loading, error } = useSelector(
    (state) => state.productDetails
  );
  const productReviews = useSelector((state) => state.reviewsFetch);

  const {
    reviews,
    error: errorReviews,
    loading: loadingReviews,
  } = productReviews;

  useEffect(() => {
    dispatch(fetchReviews(id));
    if (Object.keys(product).length === 0) {
      dispatch(listProductDetails(id));
    }
  }, [dispatch, id, product]);

  const addToCarThandler = () => {
    console.log(id, qty);
    history.push(`/cart/${id}?qty=${qty}`);
    setTimeout(() => {
      history.push('/');
    }, 1000);
  };

  return (
    <>
      <Link>
        <Button onClick={() => history.goBack()}>Go Back</Button>{' '}
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger" msg={error} />
      ) : (
        <>
          <Row className="mt-5">
            <Col md={6}>
              <Image src={product.image} alt={product.name} thumbnail />
            </Col>
            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h2>{product.name}</h2>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Rating
                    value={product.rating}
                    text={`${product.num_reviews} Reviews`}
                  />
                </ListGroup.Item>
                <ListGroup.Item>
                  <h5>
                    Price:
                    {product.price}
                  </h5>
                </ListGroup.Item>
                <ListGroup.Item>
                  Description: {product.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>
            <Col>
              <ListGroup>
                <ListGroup.Item>
                  <Row>
                    <Col>Price:</Col>
                    <Col>{product.price}</Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>Status:</Col>
                    <Col>
                      {product.in_stock > 0 ? `In Stock` : `Out of Stock`}
                    </Col>
                  </Row>
                </ListGroup.Item>
                {product.in_stock > 0 && (
                  <ListGroup.Item>
                    <Row>
                      <Col>Qty</Col>
                      <Col>
                        <Form.Control
                          as="select"
                          value={qty}
                          onChange={(e) => setQty(e.target.value)}
                        >
                          {[...Array(product.in_stock).keys()].map((index) => (
                            <option key={index + 1}>{index + 1}</option>
                          ))}
                        </Form.Control>
                      </Col>
                    </Row>
                  </ListGroup.Item>
                )}
                <ListGroup.Item>
                  <Button
                    className="btn-block"
                    disabled={product.in_stock === 0}
                    onClick={() => addToCarThandler(id, qty)}
                  >
                    Add to Cart
                  </Button>
                </ListGroup.Item>
              </ListGroup>
            </Col>
          </Row>
          <Row>
            <Col md={3}>
              <h2>Reviews</h2>
            </Col>
          </Row>
          <Row>
            <Col md={6}>
              <ListGroup variant="flush">
                {reviews.map((review) => (
                  <ListGroup.Item>
                    <pre>Seishin</pre>{' '}
                    <Rating value={5} text={`${product.num_reviews} Reviews`} />
                    <p>2020-10-15</p>
                    <pre>This is soo coool!</pre>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Col>
          </Row>
        </>
      )}
    </>
  );
};

export default ProductScreen;
