/* eslint-disable camelcase */
import React, { useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { FaDollarSign } from 'react-icons/fa';
import Message from '../Message';
import Loader from '../Loader';
import { getOrderedItemsByOrderId } from '../../actions/orderActions';

const OrderScreen = () => {
  const history = useHistory();
  const dispatch = useDispatch();
  const { id } = useParams();

  const { firstname, lastname } = useSelector(
    (state) => state.userDetails.user
  );

  const {
    userInfo: { user_id, email },
  } = useSelector((state) => state.userLogin);

  const { orders, shippingDetails, loading, error } = useSelector(
    (state) => state.orderedItems
  );

  // TODO - CALCULATIONS FOR PRICE

  const addDecimals = (num) => (Math.round(num * 100) / 100).toFixed(2);

  const subTotalPrice = addDecimals(
    orders.reduce(
      (accu, item) => accu + parseInt(item.price) * item.quantity,
      0
    )
  );
  const shippingPrice = addDecimals(subTotalPrice > 100 ? 0 : 100);
  const taxPrice = addDecimals(Number((0.15 * subTotalPrice).toFixed(2)));

  const totalPrice = (
    Number(subTotalPrice) +
    Number(shippingPrice) +
    Number(taxPrice)
  ).toFixed(2);

  useEffect(() => {
    dispatch(getOrderedItemsByOrderId(id));
  }, [dispatch, id]);

  return (
    <>
      {error && <Message variant="danger">{error}</Message>}

      <Row>
        {loading && <Loader />}
        <Col md={8}>
          <Message variant="info">
            <h5>ORDER {id}</h5>
          </Message>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Shipping</h2>
              <p>
                <strong>Name:</strong>
                {firstname}, {lastname}
              </p>
              <p>
                <strong>Email:</strong>
                {shippingDetails.payment_email_address}
              </p>
              <p>
                <strong>Address:</strong>
                {shippingDetails.address},{shippingDetails.city}{' '}
                {shippingDetails.postalCode} {shippingDetails.country}
              </p>
              {shippingDetails.is_delivered ? (
                <Message variant="success">Delivered</Message>
              ) : (
                <Message variant="danger">Not Delivered</Message>
              )}
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Payment Method</h2>
              <strong>Method:</strong>
              {shippingDetails.payment_method}
              {shippingDetails.is_paid ? (
                <Message variant="success">Paid</Message>
              ) : (
                <Message variant="danger">Not Paid</Message>
              )}
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Order List</h2>
              {orders.length === 0 ? (
                <Message>No items found!</Message>
              ) : (
                <ListGroup variant="flush">
                  {orders.map((item) => (
                    <ListGroup.Item key={item.product}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.image}
                            alt={item.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          <Link to="/product/<FaDollarSign />{item.product}">
                            {item.name}
                          </Link>
                        </Col>
                        <Col md={4}>
                          {item.quantity} x <FaDollarSign />
                          {item.price} = <FaDollarSign />
                          {item.quantity * item.price}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summary</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Subtotal</Col>
                  <Col>
                    <FaDollarSign />
                    {subTotalPrice}
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Shipping</Col>
                  <Col>
                    <FaDollarSign />
                    {shippingPrice}
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Tax</Col>
                  <Col>
                    <FaDollarSign />
                    {taxPrice}
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Total</Col>
                  <Col>
                    <FaDollarSign />
                    {totalPrice}
                  </Col>
                </Row>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default OrderScreen;
