import React, { useState, useEffect, useRef } from 'react';
import { Form } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { filterProducts } from '../actions/productActions';

const SearchBox = () => {
  const dispatch = useDispatch();
  const [search, setSearch] = useState('');
  const productsObject = useSelector((state) => state.productList);
  const { products } = productsObject;
  let filteredProducts = [];

  const searchHandler = (e) => {
    setSearch(e.target.value.substr(0, 50).toLowerCase());

    filteredProducts = products.filter(
      (product) => product.name.toLowerCase().indexOf(search) !== -1
    );
    dispatch(filterProducts(filteredProducts));
  };

  //   useEffect(() => {
  //     dispatch(filterProducts(filteredProducts));
  //   }, [filteredProducts, dispatch]);

  return (
    <div>
      <Form.Control
        type="text"
        placeholder="Search..."
        value={search}
        onChange={searchHandler}
      />
    </div>
  );
};

export default SearchBox;
