import React, { useState, useEffect, useRef } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { filterProducts, listProducts } from '../actions/productActions';

const SearchBox = () => {
  const history = useHistory();
  const [search, setSearch] = useState('');
  const productsObject = useSelector((state) => state.productList);
  const { products } = productsObject;

  const searchHandler = (e) => {
    e.preventDefault();
    if (search.trim()) {
      history.push(`/search/${search}`);
    } else {
      history.push('/');
    }
  };

  return (
    <Form onSubmit={searchHandler} inline>
      <Form.Control
        type="text"
        placeholder="Search..."
        value={search}
        onChange={(e) => setSearch(e.target.value.substr(0, 20).toLowerCase())}
        className="mr -sm-2 ml-sm-5"
      />
      <Button type="submit" variant="outline-success" className="p-2">
        Search
      </Button>
    </Form>
  );
};

export default SearchBox;
