import { productConstants } from '../constants/productConstants'

const {PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS, PRODUCT_LIST_FAIL,PRODUCT_DETAILS_REQUEST,PRODUCT_DETAILS_SUCCESS,PRODUCT_DETAILS_FAIL} = productConstants

export const productListReducer = (state = {products: []}, {type, payload}) => {
    switch (type) {
        case PRODUCT_LIST_REQUEST:
            return { loading: true, products: []}
            break;

        case PRODUCT_LIST_SUCCESS:
            return { loading: false, products: payload}
            break;   
    
        case PRODUCT_LIST_FAIL:
            return {loading: false, error: payload}
            break;   

        default:
            return state;
            break;
    }
}


export const productDetailsReducer = (state = {product: {}}, {type, payload}) => {
    switch (type) {
        case PRODUCT_DETAILS_REQUEST:
            return { ...state, loading: true}
            break;

        case PRODUCT_DETAILS_SUCCESS:
            return { ...state, loading: false, product: payload}
            break;   
    
        case PRODUCT_DETAILS_FAIL:
            return {...state, loading: false, error: payload}
            break;   

        default:
            return state;
            break;
    }
}
