import React,{useState,useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import { Link, useParams } from 'react-router-dom'
import { Button, Card, Row, Col, Image, ListGroup, Dropdown } from 'react-bootstrap'
import { FaDollarSign } from 'react-icons/fa'
import Rating from '../Rating'
import {listProductDetails} from '../../actions/productActions'


const ProductScreen = ({history, location}) => {
    // const [product, setProduct] = useState([]);
    // const  {image, name, rating, numReviews, price, description, countInStock} = product;
    // const StocksCount = [];
    // let x = 0;
    // for(x = 0 ; x <= countInStock; x++){
    //     StocksCount.push(`${x + 1}`)
    // }
    const {id} = useParams();
    const dispatch = useDispatch();
    const {product} = useSelector(state => state.productDetails)
    
    useEffect(() => {
        console.log('test')
        dispatch(listProductDetails(id));

    }, [dispatch, id])


    return (
        <>
        <Link><Button onClick={() => history.push('/')}>Go Back</Button> </Link>
        <Row className="mt-5">
            <Col md={6}>
                <Image src={product.image} alt={product.name} thumbnail/>
            </Col>
            <Col md={3}>
            <ListGroup variant="flush">
                <ListGroup.Item><h2>{product.name}</h2></ListGroup.Item>
                <ListGroup.Item><Rating value={product.rating} text={`${product.numReviews} Reviews`}/></ListGroup.Item>
                <ListGroup.Item><h5>Price: <FaDollarSign />{product.price}</h5></ListGroup.Item>
                <ListGroup.Item>Description: {product.description}</ListGroup.Item>
            </ListGroup>
            </Col>
            <Col>
            <ListGroup>
                <ListGroup.Item>
                    <Row>
                        <Col>Price:</Col>
                        <Col><FaDollarSign/>{product.price}</Col>
                    </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                    <Row>
                        <Col>Status:</Col>
                         <Col>{product.countInStock > 0 ? `In Stock` : `Out of Stock`}</Col>
                    </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                    <Row>
                        <Col>Quantity:</Col>
                        {/* <Col>
                         <Dropdown>
                            <Dropdown.Toggle variant="default" id="dropdown-basic">
                                Dropdown Button
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                                {product.StocksCount.map(num => (
                                    <Dropdown.Item value={num}>{num}</Dropdown.Item>
                                ))}
                            </Dropdown.Menu>
                          </Dropdown>
                        </Col> */}
                    </Row>
                </ListGroup.Item>
                <ListGroup.Item><Button className='btn-block' disabled={product.countInStock === 0}>Add to Cart</Button></ListGroup.Item>
            </ListGroup>
            </Col>
        </Row>
        </>
        
    )
}

export default ProductScreen
