import React from 'react'
import {BsStarFill, BsStarHalf, BsStar} from 'react-icons/bs'

const Rating = ({value, text}) => {
    const stars = [[1][0.5], [2][1.5], [3][2.5], [4][3.5], [5][4.5]];
    return (
        <div className='rating'>
            {stars.map(star => (
                <span>{value >= star[0] ? <BsStarFill/> : value >= star[1] ? <BsStarHalf/> : <BsStar/>}</span>
            ))}
            {/* <span>{value >= 1 ? <BsStarFill/> : value >= 0.5 ? <BsStarHalf/> : <BsStar/>}</span>
            <span>{value >= 2 ? <BsStarFill/> : value >= 1.5 ? <BsStarHalf/> : <BsStar/>}</span>
            <span>{value >= 3 ? <BsStarFill/> : value >= 2.5 ? <BsStarHalf/> : <BsStar/>}</span>
            <span>{value >= 4 ? <BsStarFill/> : value >= 3.5 ? <BsStarHalf/> : <BsStar/>}</span>
            <span>{value >= 5 ? <BsStarFill/> : value >= 4.5 ? <BsStarHalf/> : <BsStar/>}</span> */}
            <span>{` ${text && text}`}</span>
        </div>
    )
}

export default Rating
