import React from 'react'
import {BsStarFill, BsStarHalf, BsStar} from 'react-icons/bs'

const Rating = ({value, text, color}) => {
    const stars = [{full:1, half:0.5}, {full:2, half:1.5}, {full:3, half:2.5}, {full:4, half:3.5}, {full:5, half:4.5}];
    return (
        <div className='rating'>
            {stars.map(({full, half}) => (
                <span style={{color}}>{value >= full ? <BsStarFill/> : value >= half ? <BsStarHalf/> : <BsStar/>}</span>
            ))}
            <span>{` ${text && text}`}</span>
        </div>
    )
}

Rating.defaultProps = {
    color: '#D4AF37'
}

export default Rating
