import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Table } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';

import {
  fetchDistinctOrderId,
  fetchOneOrder,
} from '../../actions/orderActions';

const OrderListScreen = () => {
  const history = useHistory;
  const dispatch = useDispatch();

  // STATES
  const [orderList, setOrderList] = useState([]);

  // Global state
  const { distinctIds } = useSelector((state) => state.orderFetchDistinct);
  const { order } = useSelector((state) => state.orderFetchOne);

  useEffect(() => {
    // TODO ~~~ I got all the distinct IDs
    dispatch(fetchDistinctOrderId());
    // eslint-disable-next-line prefer-const
    let store = distinctIds.map((idObj) => {
      dispatch(fetchOneOrder(idObj.order_id));
      return order;
    });
  }, [dispatch]);

  useEffect(() => {
    console.log(order);
    setOrderList((prev) => [...prev, order]);
  }, [order]);

  return (
    <div>
      <h1>Orders</h1>
      {distinctIds && (
        <ul>
          {distinctIds.map((id) => (
            <li>{id.order_id}</li>
          ))}
        </ul>
      )}
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>ID</th>
            <th>USER</th>
            <th>DATE</th>
            <th>TOTAL</th>
            <th>PAID</th>
            <th>DELIVERED</th>
            <th colSpan="2" className="text-center">
              ACTIONS
            </th>
          </tr>
        </thead>
        <tbody>
          {orderList.map((order) => (
            <tr>
              <td />
              <td />
              <td />
              <td />
              <td />
              <td />
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default OrderListScreen;
